mapper.insert:
INSERT INTO tb_repair (
	inpt_id, inpt_dt
	, repair_nm
	, repair_req_dt
	, repair_res_dt
	, repair_desc
)
VALUES (
	#{inpt_id}, NOW()
	, #{repair_nm}
	, #{repair_req_dt}
	, #{repair_res_dt}
	, #{repair_desc}
)

mapper.select:
SELECT a.repair_no, a.repair_nm, a.repair_req_dt, a.repair_res_dt, a.repair_desc
FROM tb_repair a
WHERE a.repair_no = #{repair_no}

mapper.update:
UPDATE tb_repair
SET updt_dt = NOW()
, updt_id = #{updt_id} 
, repair_nm = #{repair_nm}
, repair_req_dt = #{repair_req_dt}
, repair_res_dt = #{repair_res_dt}
, repair_desc = #{repair_desc}
WHERE repair_no = #{repair_no}

UPDATE tb_repair
SET updt_dt = NOW()
, updt_id = #{updt_id} 
, del_yn = 'Y'
WHERE repair_no = #{repair_no}

UPDATE tb_repair
SET updt_dt = NOW()
, updt_id = #{updt_id}
, del_yn = 'Y'
WHERE repair_no IN (

		#{item}

)
