mapper.select:
SELECT user_id as username, user_pwd as password, user_role as userrole, IFNULL(use_yn,'N') as enableyn
FROM tb_user
WHERE user_id = #{user_id}

SELECT user_id, user_name, user_role, email, celnum
FROM tb_user
WHERE user_id = #{user_id}

mapper.insert:
 INSERT INTO tb_user (
 	user_id, user_pwd, user_name, user_role, email, celnum, use_yn, inpt_id, inpt_dt
	)
 VALUES (
 	#{user_id}, #{user_pwd}, #{user_name}, #{user_role}, #{email}, #{celnum}, #{use_yn}, #{inpt_id}, NOW()
	)
